import React, { Component } from "react";
import axios from "axios";

class LinkSaver extends Component {
  constructor(props) {
    super(props);
    this.state = {
      link: "",
      id: localStorage.getItem("id"),
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  handleSubmit(event) {
    event.preventDefault();
    const { link, id } = this.state;
    const urls = this.state.urls.url;
    axios
      .post("http://127.0.0.1:8000/api/urls/", {
        link,
        id,
      })
      .then((res) => {
        console.log(res);
        axios.get("http://127.0.0.1:8000/api/urls/" + id).then((res) => {
          localStorage.setItem("urls", JSON.stringify(res.data.url));
        });
      });
  }

  render() {
    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <input
            name="link"
            value={this.state.link}
            onChange={this.handleChange}
            placeholder="add link"
          ></input>
          <button type="submit">add</button>
        </form>
      </div>
    );
  }
}

export default LinkSaver;
