import React, { Component } from "react";
import axios from "axios";
class Edit extends Component {
  constructor(props) {
    super(props);
    this.state = {
      url1: "",
      name: "",
      user: localStorage.getItem("id"),
      urls: [],
    };
    this.handleChange = this.handleChange.bind(this);
    this.renameUrl = this.renameUrl.bind(this);
    this.deleteUrl = this.deleteUrl.bind(this);
  }

  handleChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  renameUrl(e) {
    const user = this.state.user;
    const { url1, name } = this.state;
    axios.put(`http://localhost:8000/api/urls/edit/${user}`, {
      url1,
      name,
    });
  }

  deleteUrl(e) {
    const user = this.state.user;
    const url = this.state.url1;
    axios.delete(`http://localhost:8000/api/urls/edit/${user}`, {
      url,
    });
  }

  componentDidMount() {
    const user = this.state.user;
    axios.get("http://127.0.0.1:8000/api/urls/" + user).then((res) => {
      this.setState({
        urls: res.data,
      });
    });
  }

  render() {
    return (
      <div>
        <form onSubmit={this.renameUrl}>
          <input
            name="url1"
            value={this.state.url1}
            onChange={this.handleChange}
            placeholder="rename url"
          ></input>
          <br />
          <input
            name="name"
            value={this.state.name}
            onChange={this.handleChange}
            placeholder="change name"
          ></input>
          <button type="submit">rename</button>
        </form>
      </div>
    );
  }
}

export default Edit;
