import React, { Component} from "react";
import { Switch, Route, Link , BrowserRouter} from "react-router-dom";
import axiosInstance from './axiosApi.js'

class Home extends Component {
    constructor(props) {
        super(props);
        this.state = {
            username: "",
            password: "",
            username1: "",
            password1: "",
            email:"",
            errors: {}
        };
        this.handleLogout = this.handleLogout.bind(this);
        this.SignupHandleChange = this.SignupHandleChange.bind(this);
        this.SignupHandleSubmit = this.SignupHandleSubmit.bind(this);
        this.LoginHandleChange = this.LoginHandleChange.bind(this);
        this.LoginHandleSubmit = this.LoginHandleSubmit.bind(this);
    }


    componentDidMount() {
        const {username, password} = this.state;
        localStorage.setItem('username', username);
        localStorage.setItem('password', password);
        const localstorageusername = localStorage.setItem('username', username);
        const localstoragepassword = localStorage.setItem('password', password);
        console.log(localstorageusername);
        console.log(localstoragepassword);
    }

    async LoginHandleSubmit(event) {
        try {
            const data = await axiosInstance.post('/token/obtain/', {
                username: this.state.username,
                password: this.state.password
            });
            axiosInstance.defaults.headers['Authorization'] = "JWT " + data.access;
            localStorage.setItem('access_token', data.access);
            localStorage.setItem('refresh_token', data.refresh);
            return data;
        } catch (error) {
            throw error;
        }
    }

    LoginHandleChange(event) {
        this.setState({[event.target.name]: event.target.value});
    }

    SignupHandleChange(event) {
        this.setState({[event.target.name]: event.target.value});
    }

    async SignupHandleSubmit(event) {
        try {
            const response = await axiosInstance.post('/user/create/', {
                username: this.state.username,
                email: this.state.email,
                password: this.state.password
            });
            return response;
        } catch (error) {
            console.log(error.stack);
            this.setState({
                errors:error.response.data
            });
        }
    }

    async handleLogout() {
        try {
            const response = await axiosInstance.post('/blacklist/', {
                "refresh_token": localStorage.getItem("refresh_token")
            });
            localStorage.removeItem('access_token');
            localStorage.removeItem('refresh_token');
            axiosInstance.defaults.headers['Authorization'] = null;
            return response;
        }
        catch (e) {
            console.log(e);
        }
    };

    render() {
        return (
            <div className="site">
                <nav>
                    <BrowserRouter>
                    <Link className={"nav-link"} to={"/"}>Home</Link>
                    <Link className={"nav-link"} to={"/login"}>Login</Link>
                    <Link className={"nav-link"} to={"/signup"}>Signup</Link>
                    <button onClick={this.handleLogout}>Logout</button>
                    </BrowserRouter>
                </nav>
                <main>
                    <BrowserRouter>
                    <Switch>
                        <Route exact path={"/login"} component={Login}/>
                        <Route exact path={"/signup"} component={Signup}/>
                        <Route path={"/"} render={() => <div>Home again</div>}/>
                   </Switch>
                    </BrowserRouter>
                    <Login 
                    username={this.state.username1} 
                    password={this.state.password1} 
                    submit={this.LoginHandleSubmit} 
                    change={this.LoginHandleChange}
                    />
                    <Signup 
                    username={this.state.username} 
                    password={this.state.password} 
                    email={this.state.email} 
                    submit={this.SignupHandleSubmit} 
                    change={this.SignupHandleChange}
                    errors={this.state.errors}
                    />
               </main>
            </div>
        );
    }
}


class Signup extends Component {
    constructor(props) {
        super(props);
    }

    render() {
        const {username, email, password, errors} = this.props;
        const handleChange = this.props.change;
        const handleSubmit = this.props.submit;

        return (
            <div>
                Signup
                <form onSubmit={handleSubmit}>
                    <label>
                        Username:
                        <input name="username" type="text" value={username} onChange={handleChange}/>
                    </label>
                    <label>
                        Email:
                        <input name="email" type="email" value={email} onChange={handleChange}/>
                    </label>
                    <label>
                        Password:
                        <input name="password" type="password" value={password} placeholder="8 characters or more" onChange={handleChange}/>
                    </label>
                    <input type="submit" value="Submit"/>
                </form>
            </div>
        )
    }
}

class Login extends Component {
    render() {
        const {username, password} = this.props;
        const handleChange = this.props.change;
        const handleSubmit = this.props.submit;
        return (
            <div>
                Login
                <form onSubmit={handleSubmit}>
                    <label>
                        Username:
                        <input name="username" type="text" value={username} onChange={handleChange}/>
                    </label>
                    <label>
                        Password:
                        <input name="password" type="password" value={password} onChange={handleChange}/>
                    </label>
                    <input type="submit" value="Submit"/>
                </form>
            </div>
        )
    }
}
export default Home;
