import React, { Component } from "react";
import axios from "axios";

class LinkSaver extends Component {
  constructor(props) {
    super(props);
    this.state = {
      url: "",
      user: localStorage.getItem("id"),
      urls: [],
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  handleSubmit() {
    const { url, user } = this.state;
    axios.post("http://127.0.0.1:8000/api/urls/", {
      url,
      user,
    });
  }

  componentDidMount() {
    const user = this.state.user;
    axios.get("http://127.0.0.1:8000/api/urls/" + user).then((res) => {
      this.setState({
        urls: res.data,
      });
    });
  }

  render() {
    const urls = this.state.urls;
    const table = document.getElementById("table");
    urls.forEach(function (item) {
      const row = document.createElement("tr");
      const link_td = document.createElement("td");
      const link = document.createElement("a");
      link.textContent = item.url;
      link.href = item.url;

      link_td.appendChild(link);

      row.appendChild(link);
      table.appendChild(row);
    });

    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <input
            name="url"
            value={this.state.url}
            onChange={this.handleChange}
            placeholder="add url"
          ></input>
          <button type="submit">add</button>
        </form>
        <table id="table">
          
        </table>
      </div>
    );
  }
}

export default LinkSaver;
