import React, { Component} from "react";
import { Switch, Route, Link , BrowserRouter, Redirect} from "react-router-dom";
import Login from "./Login";
import Signup from "./Signup";
import axiosInstance from './axiosApi.js'
import '../styles/main.css'
class Home extends Component {
    constructor(props) {
        super(props);
        this.state = {
            username: '',
            password: '',
            link: '',
        }
        this.handleLogout = this.handleLogout.bind(this);
    }

    async lol() {
        try {
            const data = await axiosInstance.get('/token/obtain/', {
                username: this.state.username,
                password: this.state.password
            });
            axiosInstance.defaults.headers['Authorization'] = "JWT " + data.access;
            localStorage.getItem('', data.access);
            localStorage.gettem('refresh_token', data.refresh);
            return data;
        } catch (error) {
            throw error;
        }
    }

    handleChange(event) {
        this.setState( event.target.value);
    }

    createLink() {
        const a = document.createElement('a');
        a.setAttribute('href',this.state.link);
    }

    async handleLogout() {
        try {
            const response = await axiosInstance.post('/blacklist/', {
                "refresh_token": localStorage.getItem("refresh_token")
            });
            localStorage.removeItem('access_token');
            localStorage.removeItem('refresh_token');
            axiosInstance.defaults.headers['Authorization'] = null;
            return response;
        }
        catch (e) {
            console.log(e);
        }
    };


    render() {
        return (
            <div className="site">
                <nav className="nav-var">
                    <BrowserRouter>
                    <Link className={"nav-link"} id="home-link" to={"/"}>Home</Link>
                    <Link className={"nav-link"} id="login-link" to={"/login"}>Login</Link>
                    <Link className={"nav-link"} id="signup-link" to={"/signup"}>Signup</Link>
                    <button onClick={this.handleLogout}>Logout</button>
                    </BrowserRouter>
                </nav>
                <main className="Main">
                    <BrowserRouter>
                    <Switch>
                        <Route exact path={"/login"} component={Login}/>
                        <Route exact path={"/signup"} component={Signup}/>
                        <Route path={"/"}/>
                   </Switch>
                    </BrowserRouter>
               </main>
               <div className="save-links">
                   <form onSubmit={this.createLink}>
                       <label>
                    <input className="link-adder" name='link' type="text" value={this.state.link} onChange={this.handleChange}></input>
                       </label>
                    <button className="link-submitter" type="submit">submit</button>
                   </form>
               </div>
            </div>
        );
    }
}

export default Home;
