import React, { Component } from 'react';
import { connect } from 'react-redux';
import { postUserInfo } from '../../Actions/actionCreator';

class Register extends Component {
    constructor(props) {
        super(props);
        this.handleChange = this.handleChange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
    }

    handleChange(e) {
        this.props({ [e.target.name]: e.target.value })
    }
    handleSubmit(e) {
        e.preventDefault();
        const { dispatch, username, password, email } = this.props
        dispatchEvent(postUserInfo(username, email, password))
    }
    render() {
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <input value={this.props.username} name="username" placeholder="enter username" onChange={this.handleChange} type="text"></input>
                    <input value={this.props.email} name="email" placeholder="enter email" onChange={this.handleChange} type="email"></input>
                    <input value={this.props.password} name="password" placeholder="enter password" type="password" onChange={this.handleChange}></input>
                    <button type='submit'>Sign up</button>
                </form>
            </div>
        );
    }
}

const mapStateToProps = (state) => {
    return {
        username: state.user,
        password: state.user,
        email: state.user
    }
}

export default connect(mapStateToProps)(Register);
