import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { createPosts } from '../Actions/userActions';

class Register extends Component {
    constructor(props) {
        super(props);
        this.state = {
            username: '',
            email: '',
            password: '',
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleSubmit(event) {
        event.preventDefault();
        this.props.createPosts(this.state)

        this.setState({ username: '', password: '', email: '' })
    }

    render() {
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <input value={this.state.email} name="email" onChange={e => this.updateInput(e.target.value)} placeholder="enter email" type="email"></input>
                    <input value={this.state.username} name="username" onChange={this.handleChange} placeholder="enter username" type="text"></input>
                    <input value={this.state.password} name="password" onChange={this.handleChange} placeholder="enter password" type="password"></input>
                    <button type="submit">Sign Up</button>
                </form>
            </div>
        );
    }
}

Register.propTypes = {
    createPosts: PropTypes.func.isRequired,
    user: PropTypes.object.isRequired
}

const mapStateToProps = state => ({
    user: state.user.items
})

export default connect(mapStateToProps, { createPosts })(Register);
