import React, { Component } from "react";
import axios from "axios";

class LinkSaver extends Component {
  constructor(props) {
    super(props);
    this.state = {
      url1: "",
      name: "",
      user: localStorage.getItem("id"),
      urls: [],
      showForm: false,
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.deleteUrl = this.deleteUrl.bind(this);
    this.renameUrl = this.renameUrl.bind(this);
  }

  handleChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  handleSubmit() {
    const { url, user, name } = this.state;
    axios.post("http://127.0.0.1:8000/api/urls/", {
      url,
      name,
      user,
    });
  }



  renameUrl(e) {
    const user = this.state.user;
    const { url1, name } = this.state;
    axios.put(`http://localhost:8000/api/urls/edit/${user}`, {
      url1,
      name,
    });
  }

  deleteUrl(e) {
    const user = this.state.user;
    const url = this.state.url1;
    axios.delete(`http://localhost:8000/api/urls/edit/${user}`, {
      url,
    });
  }

  renameButton() {
    return (
      <div>
        <form onSubmit={this.renameUrl}>
          <input
            name="url1"
            value={this.state.url1}
            onChange={this.handleChange}
            placeholder="rename url"
          ></input>
        </form>
      </div>
    );
  }

  componentDidMount() {
    const user = this.state.user;
    axios.get("http://127.0.0.1:8000/api/urls/" + user).then((res) => {
      this.setState({
        urls: res.data,
      });
    });
  }

  render() {
    const urls = this.state.urls;
    const { showForm } = this.state;
    const url2 = urls.map(function (items) {
      return (
        <thead key={items.id}>
          <tr>
            <th>
              <a href={items.url}>{items.name}</a>
            </th>
            <th>
              <button>rename</button>
            </th>
            <th>
              <button>delete</button>
            </th>
          </tr>
        </thead>
      );
    });
    return (
      <div>
        {showForm && this.renameButton}
        <form onSubmit={this.handleSubmit}>
          <input
            name="url"
            value={this.state.url}
            onChange={this.handleChange}
            placeholder="add url"
          ></input>
          <input
            name="name"
            value={this.state.name}
            onChange={this.handleChange}
            placeholder="add name"
          ></input>
          <button type="submit">add</button>
        </form>
        <div></div>
        <table id="table">{url2}</table>
      </div>
    );
  }
}

export default LinkSaver;
