import React, { Component } from 'react';
import axios from 'axios';


class linkSaver extends Component {
    constructor(props) {
        super(props);
        this.state = {
            link: '',
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange = (e) => {
        this.setState({ [e.target.name]: e.target.value });
    }

    handleSubmit(event) {
        event.preventDefault();
        const link = this.state.link;
        axios.post('http://127.0.0.1:8000/api/urls/', {
            link,
            "access_token": localStorage.getItem("access_token")
        }).then(res => {
            console.log(res)
            console.log(res.data)
        })
    }

    handleToken(event) {
        event.preventDefault();
        const token = this.props.access_token;
        console.log(token)
    }

    render() {
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <input name="link" value={this.state.link} onChange={this.handleChange} placeholder="add link"></input>
                    <button type="submit">add</button>
                </form>
                <button onClick={this.handleToken}></button>
            </div>
        );
    }
}

export default linkSaver;
